async function loadSupabaseScript() {
  try {
    return await new Promise((resolve, reject) => {
      const script = document.createElement('script');
      script.src = '/js/supabase.min.js';
      script.timeout = 10000;
      script.onload = () => {
        console.log('Local Supabase script loaded successfully');
        resolve();
      };
      script.onerror = () => {
        console.error('Failed to load local Supabase script');
        reject(new Error('Failed to load local Supabase script'));
      };
      document.head.appendChild(script);
    });
  } catch (error) {
    console.error(error.message);
    throw new Error('Failed to load Supabase script');
  }
}
